import Testing

@Suite("LeetCode1915") struct LeetCode1915Tests {
  private let problem = LeetCode1915()

  @Test(
    arguments: [
      ("aba", 4),
      ("aabb", 9),
      ("he", 2),
      ("ababcca", 19),
    ],
  )
  func wonderfulSubstrings(input: (String, Int)) {
    let result = problem.wonderfulSubstrings(input.0)
    #expect(result == input.1, "Input: \(input.0), Expected: \(input.1), Got: \(result)")
  }

  @Test func wonderfulSubstringsLongString() {
    let result = problem.wonderfulSubstrings("")
    #expect(result == 516_375, "Expected: 516375, Got: \(result)")
  }
}
